<script>
  $(function(){
    $(".top_xxk li a").removeClass("active")
    $(".top_xxk li").eq(0).children("a").addClass("active")
    $(".bottom_xxk .rowactive").hide()
    $(".bottom_xxk .rowactive").eq(0).show()
