      $(".top_xxk li a").removeClass("active")
      $(this).children("a").addClass("active")
      n = $(".top_xxk li").index(this)
      $(".bottom_xxk .rowactive").hide()
      $(".bottom_xxk .rowactive").eq(n).show()
      $("#container_body").HoverTreeScroll(1000);

    });
  });
</script>

